'use client';
import Container from '@/components/Container';
import { Button } from '@/components/ui/button';
import FarmHeading from '@/views/FarmView/commons/FarmHeading/FarmHeading';
import Image from 'next/image';
import Link from 'next/link';
import { usePathname } from 'next/navigation';
import { PropsWithChildren, useState } from 'react';
type FarmPages = 'hatom' | 'ashswap' | 'farm' | 'one-dex';
const FarmLayout = ({ children }: PropsWithChildren) => {
  const pathname = usePathname();
  const [selectedProtocol, setSelectedProtocol] = useState<FarmPages>(
    pathname.split('/').at(-1) as FarmPages
  );
  return (
    <Container className='xl:max-w-[1200px]'>
      <div className='flex flex-col items-center text-center mt-5'>
        <FarmHeading />
        <div className='flex flex-col gap-3 w-full '>
          <div className='flex flex-wrap gap-3 items-center mt-10 justify-center'>
            {/* Bsk protocol */}
            <ProtolContainer
              onClick={() => setSelectedProtocol('farm')}
              selected={selectedProtocol === 'farm'}
              href={'/normie/farms'}
            >
              <div className='flex gap-2 items-center'>
                <Image
                  src={'/assets/img/bsk-logo.svg'}
                  alt='hatom'
                  width={26}
                  height={26}
                />
                <p>BSK</p>
              </div>
            </ProtolContainer>

            <ProtolContainer
              onClick={() => setSelectedProtocol('one-dex')}
              selected={selectedProtocol === 'one-dex'}
              href={'/normie/farms' + '/one-dex'}
            >
              <Image
                src={
                  'data:image/png;base64,'
                }
                alt='hatom'
                width={80}
                height={26}
              />
            </ProtolContainer>

            {/* Hatom protocol */}
            <ProtolContainer
              onClick={() => setSelectedProtocol('hatom')}
              selected={selectedProtocol === 'hatom'}
              href={'/normie/farms' + '/hatom'}
            >
              <Image
                src={'/assets/img/hatom-text-white.png'}
                alt='hatom'
                width={80}
                height={26}
                className='hidden dark:block'
              />
              <Image
                src={'/assets/img/hatom-text-black.png'}
                alt='hatom'
                width={80}
                height={26}
                className='block dark:hidden'
              />
            </ProtolContainer>

            {/* Ashfarm protocol */}
            <ProtolContainer
              onClick={() => setSelectedProtocol('ashswap')}
              selected={selectedProtocol === 'ashswap'}
              href={'/normie/farms' + '/ashswap'}
            >
              <div className='flex gap-2 items-center'>
                <Image
                  src={
                    'https://app.ashswap.io/_next/static/media/logo-ashswap.7f385bbf.png'
                  }
                  alt='ashswap'
                  width={70}
                  height={24}
                  className='w-7 h-auto'
                />
                ashswap
              </div>
            </ProtolContainer>
          </div>

          {children}
        </div>
      </div>
    </Container>
  );
};

export default FarmLayout;

const ProtolContainer = ({
  children,
  onClick,
  selected,
  href
}: PropsWithChildren<{
  onClick: () => void;
  selected: boolean;
  href: string;
}>) => {
  return (
    <Button
      asChild
      variant={selected ? 'secondary' : 'outline'}
      className='cursor-pointer w-[150px]'
      onClick={onClick}
    >
      <Link href={href}>
        <div className='px-2 py-1  rounded-md '>{children}</div>
      </Link>
    </Button>
  );
};
